import React, { useState, useEffect } from 'react';
import { useConstruction } from '../../context/ConstructionContext';
import '../../styles/pages.css';

const Construction = () => {
  const {
    projectData,
    stages,
    updateStage,
    overallProgress,
    calculateStageAmount,
    totalConstructionCost,
    paidConstructionAmount,
    balanceConstructionAmount
  } = useConstruction();

  const [editingStageId, setEditingStageId] = useState(null);
  const [editForm, setEditForm] = useState({});

  const handleEditClick = (stage) => {
    setEditingStageId(stage.id);
    setEditForm({
      ...stage,
      amount: stage.amount.toString()
    });
  };

  const handleCancelEdit = () => {
    setEditingStageId(null);
    setEditForm({});
  };

  const handleSaveEdit = (e) => {
    e.preventDefault();
    updateStage(editingStageId, {
      ...editForm,
      amount: parseInt(editForm.amount)
    });
    setEditingStageId(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => {
      const newState = { ...prev, [name]: value };

      // Auto-calculate amount if percentage changes
      if (name === 'percentage') {
        newState.amount = calculateStageAmount(parseInt(value) || 0).toString();
      }

      return newState;
    });
  };

  return (
    <div className="construction-page">
      <div className="construction-bg"></div>
      <div className="container">
        <div className="page-header">
          <h1>Construction Stages</h1>
          <div className="header-actions">
            <button className="btn btn-primary" onClick={() => window.print()}>
              <i className="fas fa-print"></i> Print Report
            </button>
          </div>
        </div>

        <div className="stats-grid">
          <div className="stat-card">
            <i className="fas fa-hard-hat"></i>
            <div className="stat-info">
              <h3>Overall Progress</h3>
              <div className="value">{Math.round(overallProgress)}%</div>
            </div>
          </div>
          <div className="stat-card">
            <i className="fas fa-coins"></i>
            <div className="stat-info">
              <h3>Total Cost</h3>
              <div className="value">₹{totalConstructionCost.toLocaleString('en-IN')}</div>
            </div>
          </div>
          <div className="stat-card">
            <i className="fas fa-check-circle"></i>
            <div className="stat-info">
              <h3>Paid Amount</h3>
              <div className="value">₹{paidConstructionAmount.toLocaleString('en-IN')}</div>
            </div>
          </div>
          <div className="stat-card">
            <i className="fas fa-balance-scale"></i>
            <div className="stat-info">
              <h3>Balance</h3>
              <div className="value">₹{balanceConstructionAmount.toLocaleString('en-IN')}</div>
            </div>
          </div>
        </div>

        <div className="stages-container">
          {stages.map((stage) => (
            <div key={stage.id} className={`stage-card ${editingStageId === stage.id ? 'editing' : ''}`}>
              {editingStageId === stage.id ? (
                <form onSubmit={handleSaveEdit} className="edit-stage-form">
                  <div className="form-row">
                    <div className="form-group">
                      <label>Stage Name</label>
                      <input
                        type="text"
                        name="name"
                        value={editForm.name}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label>Percentage (%)</label>
                      <input
                        type="number"
                        name="percentage"
                        value={editForm.percentage}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label>Status</label>
                      <select
                        name="status"
                        value={editForm.status}
                        onChange={handleInputChange}
                        className="form-control"
                      >
                        <option value="pending">Not Started</option>
                        <option value="in-progress">In Progress</option>
                        <option value="completed">Completed</option>
                      </select>
                    </div>
                    <div className="form-group">
                      <label>Date</label>
                      <input
                        type="date"
                        name="date"
                        value={editForm.date}
                        onChange={handleInputChange}
                        className="form-control"
                      />
                    </div>
                  </div>

                  <div className="form-group">
                    <label>Description</label>
                    <textarea
                      name="notes"
                      value={editForm.notes}
                      onChange={handleInputChange}
                      className="form-control"
                      rows="2"
                    />
                  </div>

                  <div className="form-group">
                    <label>Amount (₹)</label>
                    <input
                      type="number"
                      name="amount"
                      value={editForm.amount}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>

                  <div className="form-actions">
                    <button type="submit" className="btn btn-success btn-sm">
                      <i className="fas fa-save"></i> Save
                    </button>
                    <button type="button" onClick={handleCancelEdit} className="btn btn-secondary btn-sm">
                      <i className="fas fa-times"></i> Cancel
                    </button>
                  </div>
                </form>
              ) : (
                <>
                  <div className="stage-header">
                    <div className="stage-info">
                      <h3>{stage.name}</h3>
                      <div className="stage-meta">
                        <span>{stage.percentage}% of total cost</span>
                        {stage.date && <span>| {stage.status === 'completed' ? 'Completed' : 'Started'} on {stage.date}</span>}
                      </div>
                    </div>
                    <div className="stage-actions">
                      <span className={`stage-status status-${stage.status}`}>
                        {stage.status === 'pending' ? 'Not Started' : stage.status.replace('-', ' ')}
                      </span>
                      <button
                        className="btn btn-secondary btn-sm"
                        onClick={() => handleEditClick(stage)}
                        title="Edit Stage"
                      >
                        <i className="fas fa-edit"></i>
                      </button>
                    </div>
                  </div>

                  <div className="stage-content">
                    <p className="stage-description">{stage.notes || "No description provided."}</p>

                    <div className="stage-financials">
                      <div className="amount-group">
                        <label>Total Amount</label>
                        <div className="amount">₹{stage.amount.toLocaleString('en-IN')}</div>
                      </div>
                      <div className="amount-group">
                        <label>Paid</label>
                        <div className="amount" style={{ color: 'var(--success)' }}>₹{stage.paid.toLocaleString('en-IN')}</div>
                      </div>
                      <div className="amount-group">
                        <label>Balance</label>
                        <div className="amount" style={{ color: 'var(--danger)' }}>₹{(stage.amount - stage.paid).toLocaleString('en-IN')}</div>
                      </div>
                    </div>

                    <div className="progress-container">
                      <div
                        className="progress-bar"
                        style={{ width: `${stage.status === 'completed' ? 100 : stage.status === 'in-progress' ? 50 : 0}%` }}
                      ></div>
                    </div>
                  </div>
                </>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Construction;
