    const saved = localStorage.getItem('construction-expenses');
    return saved ? JSON.parse(saved) : INITIAL_EXPENSES;
  });
  const [clients, setClients] = useState(() => {
    const saved = localStorage.getItem('construction-clients');
    return saved ? JSON.parse(saved) : CLIENTS_DATA;
  });
  const [payments, setPayments] = useState(() => {
    const saved = localStorage.getItem('construction-payments');
    return saved ? JSON.parse(saved) : [];
  });
  const [expensePayments, setExpensePayments] = useState(() => {
    const saved = localStorage.getItem('expense-payments');
    return saved ? JSON.parse(saved) : [];
  });

  // Save to localStorage whenever data changes
  useEffect(() => {
    localStorage.setItem('construction-stages', JSON.stringify(stages));
  }, [stages]);

  useEffect(() => {
    localStorage.setItem('construction-expenses', JSON.stringify(expenses));
  }, [expenses]);

  useEffect(() => {
    localStorage.setItem('construction-clients', JSON.stringify(clients));
  }, [clients]);

  useEffect(() => {
    localStorage.setItem('construction-payments', JSON.stringify(payments));
  }, [payments]);

  useEffect(() => {
    localStorage.setItem('expense-payments', JSON.stringify(expensePayments));
  }, [expensePayments]);

  // Calculate stage amount based on percentage
  const calculateStageAmount = (percentage) => {
    return Math.round((percentage / 100) * projectData.totalCost);
  };

  // Calculate derived data
  const totalConstructionCost = projectData.totalCost;
  const paidConstructionAmount = stages.reduce((sum, stage) => sum + stage.paid, 0);
  const balanceConstructionAmount = totalConstructionCost - paidConstructionAmount;
  
  const totalExpensesAmount = expenses.reduce((sum, expense) => sum + expense.amount, 0);
  const paidExpensesAmount = expenses.reduce((sum, expense) => sum + expense.paid, 0);
  const balanceExpensesAmount = totalExpensesAmount - paidExpensesAmount;

  const overallProgress = stages.reduce((sum, stage) => {
    return stage.status === 'completed' ? sum + stage.percentage : sum;
  }, 0);

  // Stage management
  const addStage = (newStage) => {
    const stageWithId = {
      ...newStage,
      id: `stage-${Date.now()}`,
      paid: newStage.paid || 0,
      status: newStage.paid > 0 ? (newStage.paid >= newStage.amount ? 'completed' : 'in-progress') : 'pending',
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };
    setStages(prev => [...prev, stageWithId]);
    return stageWithId;
  };

  const updateStage = (stageId, updatedStage) => {
    setStages(prev => prev.map(stage => {
      if (stage.id === stageId) {
        const updated = { ...stage, ...updatedStage, updatedAt: new Date().toISOString() };
        // Auto-update status based on payment
        if (updated.paid >= updated.amount) {
          updated.status = 'completed';
        } else if (updated.paid > 0) {
          updated.status = 'in-progress';
        } else {
          updated.status = 'pending';
        }
        return updated;
      }
      return stage;
    }));
  };

  const deleteStage = (stageId) => {
    setStages(prev => prev.filter(stage => stage.id !== stageId));
    // Also remove associated payments
    setPayments(prev => prev.filter(payment => payment.itemId !== stageId));
  };

  // Expense management
  const addExpense = (newExpense) => {
    const expenseWithId = {
      ...newExpense,
      id: Date.now(),
      paid: newExpense.paid || 0,
      status: newExpense.paid > 0 ? (newExpense.paid >= newExpense.amount ? 'paid' : 'pending') : 'pending',
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };
    setExpenses(prev => [...prev, expenseWithId]);
    return expenseWithId;
  };

  const updateExpense = (expenseId, updatedExpense) => {
    setExpenses(prev => prev.map(expense => {
      if (expense.id === expenseId) {
        const updated = { ...expense, ...updatedExpense, updatedAt: new Date().toISOString() };
        // Auto-update status based on payment
        if (updated.paid >= updated.amount) {
          updated.status = 'paid';
        } else {
          updated.status = 'pending';
        }
        return updated;
      }
      return expense;
    }));
  };

  const deleteExpense = (expenseId) => {
    setExpenses(prev => prev.filter(expense => expense.id !== expenseId));
    setExpensePayments(prev => prev.filter(payment => payment.itemId !== expenseId));
  };

  // Payment management
  const addPayment = (paymentData) => {
    const newPayment = {
      ...paymentData,
      id: Date.now(),
      date: paymentData.date || new Date().toISOString().split('T')[0],
      createdAt: new Date().toISOString()
    };
    
    if (paymentData.type === 'construction') {
      setPayments(prev => [...prev, newPayment]);
      
      // Update stage payment status
      const stage = stages.find(s => s.id === paymentData.itemId);
      if (stage) {
        const newPaid = stage.paid + paymentData.amount;
        updateStage(paymentData.itemId, {
          paid: newPaid
        });
      }
    } else {
      setExpensePayments(prev => [...prev, newPayment]);
      
      // Update expense payment status
      const expense = expenses.find(e => e.id === paymentData.itemId);
      if (expense) {
        const newPaid = expense.paid + paymentData.amount;
        updateExpense(paymentData.itemId, {
          paid: newPaid
        });
      }
    }

    return newPayment;
  };

  const updatePayment = (paymentId, updatedData, type = 'construction') => {
    if (type === 'construction') {
      setPayments(prev => prev.map(payment => 
        payment.id === paymentId ? { ...payment, ...updatedData } : payment
      ));
    } else {
      setExpensePayments(prev => prev.map(payment => 
        payment.id === paymentId ? { ...payment, ...updatedData } : payment
      ));
    }
  };

  const deletePayment = (paymentId, type = 'construction') => {
    if (type === 'construction') {
      setPayments(prev => prev.filter(payment => payment.id !== paymentId));
    } else {
      setExpensePayments(prev => prev.filter(payment => payment.id !== paymentId));
    }
  };

  // Client management
  const addClient = (newClient) => {
    const clientWithId = {
      ...newClient,
      id: Date.now(),
      joinDate: new Date().toISOString().split('T')[0],
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };
    setClients(prev => [...prev, clientWithId]);
    return clientWithId;
  };

  const updateClient = (clientId, updatedClient) => {
    setClients(prev => prev.map(client => 
      client.id === clientId ? { 
        ...client, 
        ...updatedClient, 
        updatedAt: new Date().toISOString() 
      } : client
    ));
  };

  const deleteClient = (clientId) => {
    setClients(prev => prev.filter(client => client.id !== clientId));
  };

  // Get all payments combined
  const getAllPayments = () => {
    const constructionPayments = payments.map(payment => ({
      ...payment,
      type: 'construction',
      itemName: stages.find(s => s.id === payment.itemId)?.name || 'Unknown Stage'
    }));
    
