      ...payment,
      type: 'expense',
      itemName: expenses.find(e => e.id === payment.itemId)?.name || 'Unknown Expense'
    }));
    
    return [...constructionPayments, ...expensePaymentsList].sort((a, b) => 
      new Date(b.date) - new Date(a.date)
    );
  };

  const value = {
    projectData,
    stages,
    expenses,
    clients,
    payments,
    expensePayments,
    totalConstructionCost,
    paidConstructionAmount,
    balanceConstructionAmount,
    totalExpensesAmount,
    paidExpensesAmount,
    balanceExpensesAmount,
    overallProgress,
    calculateStageAmount,
    addStage,
    updateStage,
    deleteStage,
    addExpense,
    updateExpense,
    deleteExpense,
    addPayment,
    updatePayment,
    deletePayment,
    addClient,
    updateClient,
    deleteClient,
    getAllPayments,
    setProjectData
  };

  return (
    <ConstructionContext.Provider value={value}>
      {children}
    </ConstructionContext.Provider>
  );
};
