import React from 'react';
import '../../styles/components.css';

const StageCard = ({ stage, onEdit, onDelete, onAddPayment }) => {
  const balance = stage.amount - stage.paid;
  const paymentPercentage = stage.amount > 0 ? Math.round((stage.paid / stage.amount) * 100) : 0;

  const getStatusIcon = () => {
    switch (stage.status) {
      case 'completed':
        return 'fas fa-check-circle';
      case 'in-progress':
        return 'fas fa-tools';
      default:
        return 'fas fa-clock';
    }
  };

  const getStatusColor = () => {
    switch (stage.status) {
      case 'completed':
        return '#27ae60';
      case 'in-progress':
        return '#f39c12';
      default:
        return '#95a5a6';
    }
  };

  const getStatusText = () => {
    switch (stage.status) {
      case 'completed':
        return 'Completed';
      case 'in-progress':
        return 'In Progress';
      default:
        return 'Pending';
    }
  };

  const getDateInfo = () => {
    if (stage.status === 'completed' && stage.date) {
      return `Completed on ${stage.date}`;
    } else if (stage.status === 'in-progress' && stage.date) {
      return `Started on ${stage.date}`;
    } else {
      return 'Not started';
    }
  };

  return (
    <div className={`stage-card ${stage.status}`}>
      <div className="stage-header">
        <div className="stage-info">
          <h3 className="stage-title">{stage.name}</h3>
          <div className="stage-meta">
            <span className="stage-percentage">{stage.percentage}% of total cost</span>
            <span className="stage-date-info">{getDateInfo()}</span>
          </div>
        </div>
        <div className="stage-status-indicator">
          <i 
            className={getStatusIcon()} 
            style={{ color: getStatusColor() }}
            title={getStatusText()}
          ></i>
          <span className="status-text">{getStatusText()}</span>
        </div>
      </div>

      <div className="stage-details">
        <div className="stage-amount">
          Total: ₹{stage.amount.toLocaleString('en-IN')}
        </div>
        
        {stage.notes && (
          <div className="stage-notes">
            <p>{stage.notes}</p>
          </div>
        )}
      </div>

      <div className="stage-payment-progress">
        <div className="progress-header">
          <span>Payment Progress</span>
          <span>{paymentPercentage}%</span>
        </div>
        <div className="progress-bar">
          <div 
            className="progress" 
            style={{ 
              width: `${paymentPercentage}%`,
              background: paymentPercentage === 100 ? '#27ae60' : '#3498db'
            }}
          ></div>
        </div>
        <div className="progress-details">
          <span>₹{stage.paid.toLocaleString('en-IN')} paid of ₹{stage.amount.toLocaleString('en-IN')}</span>
        </div>
      </div>

      <div className="stage-payment-info">
        <div className="payment-detail">
          <span className="detail-label">Paid:</span>
          <span className="detail-value paid">₹{stage.paid.toLocaleString('en-IN')}</span>
        </div>
        <div className="payment-detail">
          <span className="detail-label">Balance:</span>
          <span className="detail-value balance">₹{balance.toLocaleString('en-IN')}</span>
        </div>
      </div>

      <div className="stage-actions">
        <button 
          className="btn btn-success btn-sm"
          onClick={onAddPayment}
          disabled={balance === 0}
          title={balance === 0 ? 'No balance remaining' : 'Add payment'}
        >
          <i className="fas fa-plus"></i> Add Payment
        </button>
        <button 
          className="btn btn-secondary btn-sm"
          onClick={onEdit}
          title="Edit stage"
        >
          <i className="fas fa-edit"></i> Edit
        </button>
        <button 
          className="btn btn-danger btn-sm"
          onClick={onDelete}
          title="Delete stage"
        >
          <i className="fas fa-trash"></i> Delete
        </button>
      </div>
    </div>
  );
};

export default StageCard;
