import React, { createContext, useContext, useState, useEffect } from 'react';

// Default Project Data
const PROJECT_DATA = {
  projectName: "PGK Construction",
  engineer: "Er. P. Govindaraj",
  perSqftRate: 1650,
  totalSqft: 625,
  totalCost: 1031250,
  lastUpdated: "2023-10-15",
  location: "Chennai, Tamil Nadu"
};

// Complete Construction Stages as requested
const INITIAL_STAGES = [
  { 
    id: "advance", 
    name: "Advance", 
    percentage: 20, 
    amount: 206250, 
    paid: 206250, 
    status: "completed", 
    date: "2023-09-01", 
    notes: "Initial payment received"
  },
  { 
    id: "basement", 
    name: "Basement", 
    percentage: 15, 
    amount: 154687, 
    paid: 50000, 
    status: "in-progress", 
    date: "2023-09-15", 
    notes: "Excavation completed, foundation work in progress"
  },
  { 
    id: "lintel", 
    name: "Lintel", 
    percentage: 15, 
    amount: 154687, 
    paid: 0, 
    status: "pending", 
    date: "", 
    notes: ""
  },
  { 
    id: "roof-level", 
    name: "Roof Level", 
    percentage: 15, 
    amount: 154687, 
    paid: 0, 
    status: "pending", 
    date: "", 
    notes: ""
  },
  { 
    id: "inner-plastering", 
    name: "Inner Plastering", 
    percentage: 10, 
    amount: 103125, 
    paid: 0, 
    status: "pending", 
    date: "", 
    notes: ""
  },
  { 
    id: "outer-plastering", 
    name: "Outer Plastering", 
    percentage: 10, 
    amount: 103125, 
    paid: 0, 
    status: "pending", 
    date: "", 
    notes: ""
  },
  { 
    id: "tiles", 
    name: "Tiles", 
    percentage: 8, 
    amount: 82500, 
    paid: 0, 
    status: "pending", 
    date: "", 
    notes: ""
  },
  { 
    id: "electrical", 
    name: "Electrical", 
    percentage: 5, 
    amount: 51562, 
    paid: 0, 
    status: "pending", 
    date: "", 
    notes: ""
  },
  { 
    id: "whitewash", 
    name: "Whitewash", 
    percentage: 2, 
    amount: 20625, 
    paid: 0, 
    status: "pending", 
    date: "", 
    notes: ""
  }
];

// Complete Expenses as requested
const INITIAL_EXPENSES = [
  { 
    id: 1, 
    name: "Septic Tank", 
    amount: 25000, 
    paid: 0, 
    date: "2023-10-05", 
    category: "septic-tank", 
    status: "pending", 
    notes: "Installation in progress",
    vendor: "Sanitation Experts"
  },
  { 
    id: 2, 
    name: "Sump", 
    amount: 15000, 
    paid: 15000, 
    date: "2023-09-20", 
    category: "sump", 
    status: "paid", 
    notes: "5000L capacity sump installed",
    vendor: "Plumbing Masters"
  },
  { 
    id: 3, 
    name: "Borewell", 
    amount: 50000, 
    paid: 50000, 
    date: "2023-09-10", 
    category: "borewell", 
    status: "paid", 
    notes: "Completed successfully with good water flow",
    vendor: "Water Solutions Inc."
  }
];

const CLIENTS_DATA = [
  {
    id: 1,
    name: "Rajesh Kumar",
    phone: "+91 9876543210",
    email: "rajesh@example.com",
    address: "123 Main Street, Chennai",
    projectType: "Residential House",
    budget: "₹25,00,000",
    status: "Active",
    joinDate: "2023-08-15"
  }
];

const ConstructionContext = createContext();

export const useConstruction = () => {
  const context = useContext(ConstructionContext);
  if (!context) {
    throw new Error('useConstruction must be used within a ConstructionProvider');
  }
  return context;
};

export const ConstructionProvider = ({ children }) => {
  const [projectData, setProjectData] = useState(PROJECT_DATA);
  const [stages, setStages] = useState(() => {
    const saved = localStorage.getItem('construction-stages');
    return saved ? JSON.parse(saved) : INITIAL_STAGES;
  });
  const [expenses, setExpenses] = useState(() => {
    const saved = localStorage.getItem('construction-expenses');
    return saved ? JSON.parse(saved) : INITIAL_EXPENSES;
  });
  const [clients, setClients] = useState(() => {
    const saved = localStorage.getItem('construction-clients');
    return saved ? JSON.parse(saved) : CLIENTS_DATA;
  });
  const [payments, setPayments] = useState(() => {
    const saved = localStorage.getItem('construction-payments');
    return saved ? JSON.parse(saved) : INITIAL_PAYMENTS;
  });

  // Save to localStorage whenever data changes
  useEffect(() => {
    localStorage.setItem('construction-stages', JSON.stringify(stages));
  }, [stages]);

  useEffect(() => {
    localStorage.setItem('construction-expenses', JSON.stringify(expenses));
  }, [expenses]);

  useEffect(() => {
    localStorage.setItem('construction-clients', JSON.stringify(clients));
  }, [clients]);

  useEffect(() => {
    localStorage.setItem('construction-payments', JSON.stringify(payments));
  }, [payments]);

  // Calculate derived data
  const totalConstructionCost = projectData.totalCost;
  const paidConstructionAmount = stages.reduce((sum, stage) => sum + stage.paid, 0);
  const balanceConstructionAmount = totalConstructionCost - paidConstructionAmount;
  
  const totalExpensesAmount = expenses.reduce((sum, expense) => sum + expense.amount, 0);
  const paidExpensesAmount = expenses.reduce((sum, expense) => sum + expense.paid, 0);
  const balanceExpensesAmount = totalExpensesAmount - paidExpensesAmount;

  const overallProgress = stages.reduce((sum, stage) => {
    return stage.status === 'completed' ? sum + stage.percentage : sum;
  }, 0);

  // Get payments by type
  const getConstructionPayments = () => {
    return payments
      .filter(payment => payment.type === 'construction')
      .sort((a, b) => new Date(b.date) - new Date(a.date));
  };

  const getExpensePayments = () => {
    return payments
      .filter(payment => payment.type === 'expense')
      .sort((a, b) => new Date(b.date) - new Date(a.date));
  };

  // Get payments for specific item
  const getPaymentsForStage = (stageId) => {
    return payments
      .filter(payment => payment.type === 'construction' && payment.itemId === stageId)
      .sort((a, b) => new Date(b.date) - new Date(a.date));
  };

  const getPaymentsForExpense = (expenseId) => {
    return payments
      .filter(payment => payment.type === 'expense' && payment.itemId === expenseId)
      .sort((a, b) => new Date(b.date) - new Date(a.date));
  };

  // Stage management
  const addStage = (newStage) => {
    const stageWithId = {
      ...newStage,
      id: `stage-${Date.now()}`,
      paid: 0,
      status: 'pending'
    };
    setStages(prev => [...prev, stageWithId]);
    return stageWithId;
  };

  const updateStage = (stageId, updatedStage) => {
    setStages(prev => prev.map(stage => 
      stage.id === stageId ? { ...stage, ...updatedStage } : stage
    ));
  };

  const deleteStage = (stageId) => {
    setStages(prev => prev.filter(stage => stage.id !== stageId));
    // Also remove associated payments
    setPayments(prev => prev.filter(payment => !(payment.type === 'construction' && payment.itemId === stageId)));
  };

  // Expense management
  const addExpense = (newExpense) => {
    const expenseWithId = {
      ...newExpense,
      id: Date.now(),
      paid: 0,
      status: 'pending'
    };
    setExpenses(prev => [...prev, expenseWithId]);
    return expenseWithId;
  };

  const updateExpense = (expenseId, updatedExpense) => {
    setExpenses(prev => prev.map(expense => 
      expense.id === expenseId ? { ...expense, ...updatedExpense } : expense
    ));
  };

  const deleteExpense = (expenseId) => {
    setExpenses(prev => prev.filter(expense => expense.id !== expenseId));
    // Also remove associated payments
    setPayments(prev => prev.filter(payment => !(payment.type === 'expense' && payment.itemId === expenseId)));
  };

  // Payment management
  const addPayment = (paymentData) => {
    const newPayment = {
      ...paymentData,
      id: Date.now(),
      status: 'completed'
    };
    
    setPayments(prev => [...prev, newPayment]);
    
    // Update stage or expense payment status
    if (paymentData.type === 'construction') {
      const stage = stages.find(s => s.id === paymentData.itemId);
      if (stage) {
        const newPaid = stage.paid + paymentData.amount;
        const newStatus = newPaid >= stage.amount ? 'completed' : 
                         newPaid > 0 ? 'in-progress' : 'pending';
        
        updateStage(paymentData.itemId, {
          paid: newPaid,
          status: newStatus
        });
      }
    } else {
      const expense = expenses.find(e => e.id === paymentData.itemId);
      if (expense) {
        const newPaid = expense.paid + paymentData.amount;
        const newStatus = newPaid >= expense.amount ? 'paid' : 'pending';
        
        updateExpense(paymentData.itemId, {
          paid: newPaid,
          status: newStatus
        });
      }
    }

    return newPayment;
  };

  const deletePayment = (paymentId) => {
    const payment = payments.find(p => p.id === paymentId);
    if (!payment) return;

    // Update the associated item's paid amount
    if (payment.type === 'construction') {
      const stage = stages.find(s => s.id === payment.itemId);
      if (stage) {
        const newPaid = Math.max(0, stage.paid - payment.amount);
        const newStatus = newPaid === 0 ? 'pending' : 
                         newPaid >= stage.amount ? 'completed' : 'in-progress';
        
        updateStage(payment.itemId, {
          paid: newPaid,
          status: newStatus
        });
      }
    } else {
      const expense = expenses.find(e => e.id === payment.itemId);
      if (expense) {
        const newPaid = Math.max(0, expense.paid - payment.amount);
        const newStatus = newPaid === 0 ? 'pending' : 
                         newPaid >= expense.amount ? 'paid' : 'pending';
        
        updateExpense(payment.itemId, {
          paid: newPaid,
          status: newStatus
        });
      }
    }

    // Remove the payment
    setPayments(prev => prev.filter(p => p.id !== paymentId));
  };

  // Client management
  const addClient = (newClient) => {
    const clientWithId = {
      ...newClient,
      id: Date.now(),
      joinDate: new Date().toISOString().split('T')[0]
    };
    setClients(prev => [...prev, clientWithId]);
    return clientWithId;
  };

  const updateClient = (clientId, updatedClient) => {
    setClients(prev => prev.map(client => 
      client.id === clientId ? { ...client, ...updatedClient } : client
    ));
  };

  const deleteClient = (clientId) => {
    setClients(prev => prev.filter(client => client.id !== clientId));
  };

  const value = {
    projectData,
    stages,
    expenses,
    clients,
    payments,
    totalConstructionCost,
    paidConstructionAmount,
    balanceConstructionAmount,
    totalExpensesAmount,
    paidExpensesAmount,
    balanceExpensesAmount,
    overallProgress,
    // Payment methods
    getConstructionPayments,
    getExpensePayments,
    getPaymentsForStage,
    getPaymentsForExpense,
    // CRUD operations
    addStage,
    updateStage,
    deleteStage,
    addExpense,
    updateExpense,
    deleteExpense,
    addPayment,
    deletePayment,
    addClient,
    updateClient,
    deleteClient
  };

  return (
    <ConstructionContext.Provider value={value}>
      {children}
    </ConstructionContext.Provider>
  );
};
