import React, { createContext, useContext, useState, useEffect } from 'react';

// Default data
const PROJECT_DATA = {
  projectName: "PGK Construction",
  engineer: "Er. P. Govindaraj",
  perSqftRate: 1650,
  totalSqft: 625,
  totalCost: 1031250,
  lastUpdated: new Date().toISOString().split('T')[0]
};

const INITIAL_STAGES = [
  {
    id: "advance",
    name: "Advance",
    percentage: 20,
    amount: 206250,
    paid: 206250,
    status: "completed",
    date: "2023-09-01",
    notes: "Initial payment received"
  },
  {
    id: "basement",
    name: "Basement",
    percentage: 15,
    amount: 154687,
    paid: 0,
    status: "in-progress",
    date: "2023-09-15",
    notes: "Excavation completed, foundation work in progress"
  },
  {
    id: "lintel",
    name: "Lintel",
    percentage: 15,
    amount: 154687,
    paid: 0,
    status: "pending",
    date: "",
    notes: "Not started"
  },
  {
    id: "roof",
    name: "Roof Level",
    percentage: 15,
    amount: 154687,
    paid: 0,
    status: "pending",
    date: "",
    notes: "Not started"
  },
  {
    id: "inner-plastering",
    name: "Inner Plastering",
    percentage: 10,
    amount: 103125,
    paid: 0,
    status: "pending",
    date: "",
    notes: "Not started"
  },
  {
    id: "outer-plastering",
    name: "Outer Plastering",
    percentage: 10,
    amount: 103125,
    paid: 0,
    status: "pending",
    date: "",
    notes: "Not started"
  },
  {
    id: "tiles",
    name: "Tiles",
    percentage: 8,
    amount: 82500,
    paid: 0,
    status: "pending",
    date: "",
    notes: "Not started"
  },
  {
    id: "electrical",
    name: "Electrical",
    percentage: 5,
    amount: 51562,
    paid: 0,
    status: "pending",
    date: "",
    notes: "Not started"
  },
  {
    id: "whitewash",
    name: "Whitewash",
    percentage: 2,
    amount: 20625,
    paid: 0,
    status: "pending",
    date: "",
    notes: "Not started"
  }
];

const INITIAL_EXPENSES = [
  {
    id: 1,
    name: "Septic Tank",
    amount: 25000,
    paid: 0,
    date: "2023-10-05",
    category: "septic-tank",
    status: "pending",
    notes: "Installation in progress"
  },
  {
    id: 2,
    name: "Sump",
    amount: 15000,
    paid: 15000,
    date: "2023-09-20",
    category: "sump",
    status: "paid",
    notes: "5000L capacity sump installed"
  },
  {
    id: 3,
    name: "Borewell",
    amount: 50000,
    paid: 50000,
    date: "2023-09-10",
    category: "borewell",
    status: "paid",
    notes: "Completed successfully with good water flow"
  }
];

const ConstructionContext = createContext();

export const useConstruction = () => {
  const context = useContext(ConstructionContext);
  if (!context) {
    throw new Error('useConstruction must be used within a ConstructionProvider');
  }
  return context;
};

export const ConstructionProvider = ({ children }) => {
  const [projectData, setProjectData] = useState(PROJECT_DATA);

  const [stages, setStages] = useState(() => {
    const saved = localStorage.getItem('construction-stages');
    return saved ? JSON.parse(saved) : INITIAL_STAGES;
  });

  const [expenses, setExpenses] = useState(() => {
    const saved = localStorage.getItem('construction-expenses');
    return saved ? JSON.parse(saved) : INITIAL_EXPENSES;
  });

  const [payments, setPayments] = useState(() => {
    const saved = localStorage.getItem('construction-payments');
    return saved ? JSON.parse(saved) : [];
  });

  // Save to localStorage whenever data changes
  useEffect(() => {
    localStorage.setItem('construction-stages', JSON.stringify(stages));
  }, [stages]);

  useEffect(() => {
    localStorage.setItem('construction-expenses', JSON.stringify(expenses));
  }, [expenses]);

  useEffect(() => {
    localStorage.setItem('construction-payments', JSON.stringify(payments));
  }, [payments]);

  // Calculate derived data
  const totalConstructionCost = projectData.totalCost;
  const paidConstructionAmount = stages.reduce((sum, stage) => sum + parseInt(stage.paid || 0), 0);
  const balanceConstructionAmount = totalConstructionCost - paidConstructionAmount;

  const totalExpensesAmount = expenses.reduce((sum, expense) => sum + parseInt(expense.amount || 0), 0);
  const paidExpensesAmount = expenses.reduce((sum, expense) => sum + parseInt(expense.paid || 0), 0);
  const balanceExpensesAmount = totalExpensesAmount - paidExpensesAmount;

  const totalProjectCost = totalConstructionCost + totalExpensesAmount;
  const totalPaid = paidConstructionAmount + paidExpensesAmount;
  const totalBalance = totalProjectCost - totalPaid;

  // Overall Progress based on PAID amount vs TOTAL cost
  const overallProgress = totalProjectCost > 0 ? (totalPaid / totalProjectCost) * 100 : 0;

  // Helper functions
  const calculateStageAmount = (percentage) => {
    return Math.round((projectData.totalCost * percentage) / 100);
  };

  // Determine status based on payment
  const determineStatus = (paid, total) => {
    if (paid === 0) return 'pending';
    if (paid >= total) return 'completed';
    return 'in-progress';
  };

  // Payment History Logic
  const addPaymentHistory = (paymentRecord) => {
    const newPayment = {
      id: Date.now(),
      ...paymentRecord
    };
    setPayments(prev => [newPayment, ...prev]);
  };

  // Stage management
  const updateStage = (stageId, updatedData) => {
    setStages(prev => prev.map(stage => {
      if (stage.id === stageId) {
        let newPaid = stage.paid;

        // If paid amount changed, record it in history
        if (updatedData.paid !== undefined && updatedData.paid !== stage.paid) {
          const diff = updatedData.paid - stage.paid;
          newPaid = updatedData.paid;

          if (diff > 0) {
            addPaymentHistory({
              itemId: stageId,
              itemName: stage.name,
              amount: diff,
              totalPaid: newPaid,
              balance: stage.amount - newPaid,
              type: 'construction',
              date: new Date().toISOString().split('T')[0]
            });
          }
        }

        // Auto-update status based on new paid amount
        const newStatus = determineStatus(newPaid, stage.amount);

        return {
          ...stage,
          ...updatedData,
          paid: newPaid,
          status: newStatus
        };
      }
      return stage;
    }));
  };

  // Expense management
  const addExpense = (newExpense) => {
    const expenseWithId = {
      ...newExpense,
      id: Date.now(),
      paid: 0,
      status: 'pending'
    };
    setExpenses(prev => [...prev, expenseWithId]);
    return expenseWithId;
  };

  const updateExpense = (expenseId, updatedData) => {
    setExpenses(prev => prev.map(expense => {
      if (expense.id === expenseId) {
        let newPaid = expense.paid;

        // If paid amount changed, record it in history
        if (updatedData.paid !== undefined && updatedData.paid !== expense.paid) {
          const diff = updatedData.paid - expense.paid;
          newPaid = updatedData.paid;

          if (diff > 0) {
            addPaymentHistory({
              itemId: expenseId,
              itemName: expense.name,
              amount: diff,
              totalPaid: newPaid,
              balance: expense.amount - newPaid,
              type: 'expense',
              date: new Date().toISOString().split('T')[0]
            });
          }
        }

        // Auto-update status based on new paid amount
        const newStatus = determineStatus(newPaid, expense.amount);

        return {
          ...expense,
          ...updatedData,
          paid: newPaid,
          status: newStatus
        };
      }
      return expense;
    }));
  };

  const getAllPayments = () => {
    return payments.sort((a, b) => new Date(b.date) - new Date(a.date));
  };

  const value = {
    // State
    projectData,
    stages,
    expenses,
    payments,

    // Calculated values
    totalConstructionCost,
    paidConstructionAmount,
    balanceConstructionAmount,

    totalExpensesAmount,
    paidExpensesAmount,
    balanceExpensesAmount,

    totalProjectCost,
    totalPaid,
    totalBalance,
    overallProgress,

    // Functions
    calculateStageAmount,
    updateStage,

    addExpense,
    updateExpense,

    getAllPayments
  };

  return (
    <ConstructionContext.Provider value={value}>
      {children}
    </ConstructionContext.Provider>
  );
};
