import React, { useState } from 'react';
import { useConstruction } from '../../context/ConstructionContext';
import '../../styles/pages.css';

const Expenses = () => {
  const {
    expenses,
    totalExpensesAmount,
    paidExpensesAmount,
    balanceExpensesAmount,
    addPayment
  } = useConstruction();

  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [selectedExpense, setSelectedExpense] = useState(null);
  const [paymentAmount, setPaymentAmount] = useState('');

  const handleAddPaymentClick = (expense) => {
    setSelectedExpense(expense);
    setPaymentAmount('');
    setShowPaymentModal(true);
  };

  const handlePaymentSubmit = (e) => {
    e.preventDefault();
    if (!selectedExpense || !paymentAmount) return;

    addPayment({
      itemId: selectedExpense.id,
      amount: parseInt(paymentAmount),
      type: 'expense',
      method: 'cash', // Default to cash for now
      description: 'Payment added manually'
    });

    setShowPaymentModal(false);
    setSelectedExpense(null);
    setPaymentAmount('');
  };

  return (
    <div className="expenses-page">
      <div className="container">
        <div className="page-header">
          <h1>Other Expenses</h1>
        </div>

        <div className="expenses-summary">
          <div className="summary-card total">
            <i className="fas fa-receipt"></i>
            <div className="summary-info">
              <h3>Total Expenses</h3>
              <div className="value">₹{totalExpensesAmount.toLocaleString('en-IN')}</div>
            </div>
          </div>
          <div className="summary-card paid">
            <i className="fas fa-check-circle"></i>
            <div className="summary-info">
              <h3>Paid Amount</h3>
              <div className="value">₹{paidExpensesAmount.toLocaleString('en-IN')}</div>
            </div>
          </div>
          <div className="summary-card balance">
            <i className="fas fa-clock"></i>
            <div className="summary-info">
              <h3>Balance Amount</h3>
              <div className="value">₹{balanceExpensesAmount.toLocaleString('en-IN')}</div>
            </div>
          </div>
        </div>

        <div className="expenses-grid">
          {expenses.map((expense) => (
            <div key={expense.id} className="expense-card">
              <div className="expense-header">
                <div>
                  <h3>{expense.name}</h3>
                  <div className="expense-date">{expense.date}</div>
                </div>
                <span className="expense-category">{expense.category}</span>
              </div>

              <div className="expense-details">
                <p className="expense-notes">{expense.notes}</p>

                <div className="expense-row total">
                  <span>Total Cost</span>
                  <span>₹{expense.amount.toLocaleString('en-IN')}</span>
                </div>
                <div className="expense-row paid">
                  <span>Paid</span>
                  <span>₹{expense.paid.toLocaleString('en-IN')}</span>
                </div>
                <div className="expense-row balance">
                  <span>Balance</span>
                  <span>₹{(expense.amount - expense.paid).toLocaleString('en-IN')}</span>
                </div>
              </div>

              <div className="expense-footer">
                <span className={`expense-status ${expense.amount === expense.paid ? 'status-paid' : 'status-pending'}`}>
                  {expense.amount === expense.paid ? 'Paid' : 'Installation in progress'}
                </span>
                {expense.amount > expense.paid && (
                  <button
                    className="btn btn-primary btn-sm"
                    onClick={() => handleAddPaymentClick(expense)}
                  >
                    <i className="fas fa-plus-circle"></i> Add Payment
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>

        {/* Payment Modal */}
        {showPaymentModal && selectedExpense && (
          <div className="modal-overlay">
            <div className="modal-content">
              <div className="modal-header">
                <h3>Add Payment for {selectedExpense.name}</h3>
                <button className="close-btn" onClick={() => setShowPaymentModal(false)}>
                  <i className="fas fa-times"></i>
                </button>
              </div>
              <form onSubmit={handlePaymentSubmit}>
                <div className="form-group">
                  <label>Payment Amount (₹)</label>
                  <input
                    type="number"
                    className="form-control"
                    value={paymentAmount}
                    onChange={(e) => setPaymentAmount(e.target.value)}
                    max={selectedExpense.amount - selectedExpense.paid}
                    placeholder={`Max: ₹${selectedExpense.amount - selectedExpense.paid}`}
                    required
                  />
                </div>
                <div className="form-actions">
                  <button type="submit" className="btn btn-success">
                    Confirm Payment
                  </button>
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowPaymentModal(false)}
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Expenses;
