import React from 'react';
import { motion } from 'framer-motion';
import { useConstruction } from '../../context/ConstructionContext';
import EditableCard from '../../components/EditableCard';

const Construction = () => {
  const { stages, updateStage } = useConstruction();

  const handleSaveStage = (id, updatedData) => {
    // Convert paid to number
    const processedData = {
      ...updatedData,
      paid: Number(updatedData.paid)
    };
    updateStage(id, processedData);
  };

  return (
    <div className="construction-page" style={{
      backgroundImage: `linear-gradient(rgba(0,0,0,0.6), rgba(0,0,0,0.6)), url('/assets/images/construction-site-bg.jpg')`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundAttachment: 'fixed',
      minHeight: 'calc(100vh - 80px)',
      padding: '2rem 0',
      color: 'white',
      overflowX: 'hidden'
    }}>
      <div className="container">
        <motion.h1
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          style={{ marginBottom: '2rem', color: 'var(--primary)', textShadow: '2px 2px 4px rgba(0,0,0,0.5)' }}
        >
          Construction Stages
        </motion.h1>

        <div className="horizontal-scroll-container" style={{
          display: 'flex',
          gap: '20px',
          overflowX: 'auto',
          padding: '20px 5px',
          scrollBehavior: 'smooth',
          WebkitOverflowScrolling: 'touch',
          scrollbarWidth: 'thin'
        }}>
          {stages.map((stage, index) => (
            <motion.div
              key={stage.id}
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: index * 0.1 }}
            >
              <EditableCard
                data={stage}
                onSave={handleSaveStage}
                type="stage"
              />
            </motion.div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Construction;
